  getPoolBalance() {
    let p = new Promise<any>((resolve, reject) => {
      let that = this;
        return  this.icoContract.getPoolBalance({gas:3000000},function(error, result) {
          if (!error) {
            let res = that.web3.fromWei(result.toString(), 'ether');
            console.log('Current Balance: ' ,res);
            resolve(res);
          } else {
            console.error(error);
            reject(error);
          }
        });
    });
    return p;
  }

