  getMyCurrentBalance() {
    let that = this;
    let p = new Promise<any>((resolve, reject) => {
      return this.web3.eth.getBalance(this.web3.eth.defaultAccount, function (error, result) {
        if (!error) {
          let res = that.web3.fromWei(result.toString(), 'ether');
          console.log('Current Balance: ' ,res);
          resolve(res);
        } else {
          console.error(error);
          reject(error);
        }
      });
    }); 
    return p;
  }


