  getMyContribution() {
    let that = this;
    let p = new Promise<any>((resolve, reject) => {
      return this.icoContract.getMyContribution({gas:3000000},function(error,result){
        if (!error) {
          let res = that.web3.fromWei(result.toString(), 'ether');
          console.log('My Contribution: ' ,res);
          resolve(res);
        } else {
          console.error(error);
          reject(error);
        }
      });

    });
    return p;
  }

